mapper.select:
SELECT * FROM (
(SELECT id, title,logo,create_date,'新闻动态' AS type,'tf_journalism' AS table_name FROM tf_journalism
 WHERE 1 = 1

  and company_id = #{company,jdbcType=BIGINT}


  and status = #{status,jdbcType=BIGINT}

ORDER BY create_date )
UNION ALL
(SELECT id, title,logo,create_date,'学术活动' AS type,'tf_learning' AS table_name FROM tf_learning
WHERE 1 = 1

  and company_id = #{company,jdbcType=BIGINT}


  and status = #{status,jdbcType=BIGINT}
 ORDER BY create_date )
UNION ALL
(SELECT id,title,logo,create_date,'媒体报道' AS type,'tf_media' AS table_name FROM tf_media
 WHERE 1 = 1

  and company_id = #{company,jdbcType=BIGINT}


  and status = #{status,jdbcType=BIGINT}
 ORDER BY create_date DESC)
UNION ALL
(SELECT id,title,logo,create_date,'展会展览' AS type,'tf_exhibition' AS table_name FROM tf_exhibition
WHERE 1 = 1

  and company_id = #{company,jdbcType=BIGINT}


  and status = #{status,jdbcType=BIGINT}
 ORDER BY create_date DESC)
) A GROUP BY type

SELECT * FROM  ${table_name} where 1 = 1
    and company_id = ${companyId}
    and status = ${status}
  limit ${firstIndex},${lastsIndex}

