  #{keyword,jdbcType=VARCHAR}, #{datasource,jdbcType=VARCHAR}, #{companyId,jdbcType=BIGINT}, 
  #{hits,jdbcType=BIGINT}, #{status,jdbcType=BIGINT}, #{content,jdbcType=LONGVARCHAR}
  )
