SELECT O.*, O.id as orderId, I.name iname, COALESCE(I.orderSumPrice,0) AS orderSumPrice , ( CASE WHEN O.payment_status = 0 AND O.order_status = 0 THEN '待付款' WHEN O.payment_status = 2 AND O.order_status = 1 AND shipping_status = 0 THEN '待发货' WHEN O.payment_status = 2 AND O.shipping_status = 2 THEN '待收货' WHEN O.payment_status = 2 AND O.order_status = 2 AND shipping_status = 5 THEN '已完成' WHEN O.payment_status = 0 AND O.order_status = 3 THEN '已取消' END ) AS orderStatus FROM tf_order O LEFT JOIN (SELECT orders, NAME, SUM((price * quantity)) AS orderSumPrice FROM tf_order_item GROUP BY orders) I ON O.id = I.orders where 1 = 1
and O.member = #{member,jdbcType=BIGINT}
and O.payment_status = #{paymentStatus}
and O.shipping_status in
#{item}
and O.order_status = #{orderStatus}
and O.sn LIKE CONCAT('%', #{ordersn}, '%')
and O.create_date BETWEEN #{begindate,jdbcType=TIMESTAMP} AND #{enddate,jdbcType=TIMESTAMP}
and O.is_member_delete = #{isMemberDelete}
and O.type != 2 group by O.id order by O.create_date desc

SELECT O.*, O.id as orderId, I.name iname, COALESCE(I.orderSumPrice,0) AS orderSumPrice , ( CASE WHEN O.payment_status = 0 AND O.order_status = 0 THEN '待付款' WHEN O.payment_status = 2 AND O.order_status = 1 AND case_status = 0 THEN '待上传' WHEN O.payment_status = 2 AND O.order_status = 1 and case_status = 1 AND shipping_status = 0 THEN '待发货' WHEN O.payment_status = 2 AND O.shipping_status = 2 THEN '待收货' WHEN O.payment_status = 2 AND O.order_status = 2 AND shipping_status = 5 THEN '已完成' WHEN O.payment_status = 0 AND O.order_status = 3 THEN '已取消' END ) AS orderStatus FROM tf_order O LEFT JOIN (SELECT orders, NAME, SUM((price * quantity)) AS orderSumPrice FROM tf_order_item GROUP BY orders) I ON O.id = I.orders where 1 = 1
and O.member = #{member,jdbcType=BIGINT}
and O.payment_status = #{paymentStatus}
and O.case_status = #{caseStatus}
and O.order_status = #{orderStatus}
and O.sn like concat('%',#{ordersn},'%')
and O.create_date >= #{begindate}
and O.create_date <= #{enddate}
and O.type = #{type}
and O.case_status = #{caseStatus}
group by O.id ORDER BY O.create_date DESC

